import {ADD_PLACE} from './touristPlaceTypes'

const initialState = {
    place_list: [],
    id: 0,
}

const touristPlaceReducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_PLACE: 
            //var new_list = state.place_list.concat(action.payload);
            return {
                ...state,
                place_list: action.payload,
                id: state.id + 1
            }
        default: return {
            state
        }
    }
}

export default touristPlaceReducer;
