import {ADD_PLACE, DELETE_PLACE} from './touristPlaceTypes';


export const addPlace = (place) => {
    return {
        type: ADD_PLACE,
        payload: place
    }
}

export const deletePlace = id => {
    return {
        type: DELETE_PLACE,
        payload: id
    }
}
