import React, { Component } from 'react';
import CreatePage from './CreatePage2';

class TouristPlace {
    constructor(id, name, address, rating, type) {
        this.id = id;
        this.name = name;
        this.address = address;
        this.rating = rating;
        this.type = type;
    }

    toString() {
        return this.name;
    }
}

class TouristApp extends Component {
    constructor(props) {
        super(props);

        this.state={
            name: null,
            address: null,
            rating: null,
            type: "beach",
            picture: null,
            typeList: ["Beach", "Hills", "Fountain", "Landmark"],
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        console.log(this.state);
    }

    handleInputChange(event) {
        const target = event.target;

        switch(target.id) {
            case "name":
                this.setState({
                    name: target.value
                })
                break
            case "address":
                this.setState({
                    address: target.value
                })
                break
            case "rating":
                this.setState({
                    rating: target.value
                })
                break
            case "type":
                this.setState({
                    type: target.value
                })
                break
            default:
        }
        console.log("test")
    }

    render() {
        return (
            <CreatePage state={this.state} handleInputChange={this.handleInputChange} handleSubmit={this.handleSubmit} />
        );
    }
}

export default TouristApp;
