import React, { Component } from 'react';
import {Col, Form, Row, Table, Button} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import {FaSort, FaSortUp, FaSortDown} from 'react-icons/fa';

import 'bootstrap/dist/css/bootstrap.min.css';
import './style/ListPage.css'

class ListPage extends Component {
    constructor(props) {
        super(props);
        this.state={

        };
    }

    

    showListView(place_list) {
        let tbody = [];
        for (var i = 0; i < place_list.length; i++) {
            let row = <tr id={place_list[i].id} key={i} className='content'>
                        <td > {place_list[i].name} </td>
                        <td > {place_list[i].address} </td>
                        <td > {place_list[i].rating} </td>
                        <td className="picture"> <img src={place_list[i].picture} alt={place_list[i].name} /> </td>
                        <td >
                            <button className="custom-btn-update">Update</button>
                            <button className="custom-btn-delete" onClick={this.props.rowDeleteById}>Delete</button>
                        </td>
                    </tr>
    
            tbody.push(row)
        }
        return tbody;
    }
    
    render() {
        let sortIcon = <FaSort style={{marginLeft: "5px"}} />;
        if(this.props.state.sort_state === 1) {
            sortIcon = <FaSortUp style={{marginLeft: "5px"}} />
        } else if(this.props.state.sort_state === 2) {
            sortIcon = <FaSortDown style={{marginLeft: "5px"}} />
        }else {
            sortIcon = <FaSort style={{marginLeft: "5px"}} />
        }
        return (
            <>
                <Form.Group>
                        <Row className='test'>
                            <Col>
                                <Form.Control id="search-box" size="lg" type="text" placeholder="Enter Name to Search" bsPrefix="custom-input" required />
                            </Col>
                        </Row>
                </Form.Group>
                <br/>
                <Table bordered hover responsive="sm">
                    <thead className="header">
                        <tr style={{textAlign: "center"}}>
                            <th>NAME</th>
                            <th>ADDRESS</th>
                            <th onClick={this.props.sortAction}>
                                RATING
                                {sortIcon}
                            </th>
                            <th>PICTURE</th>
                            <th>ACTION</th>
                        </tr>
                    </thead>
                    <tbody  className="main2">
                        {this.showListView(this.props.sort())}
                    </tbody>
                </Table>
                <Link to="/create"><Button bsPrefix="custom-btn">Create New Tourist Place</Button></Link>
            </>
        );
    }
}

export default ListPage;
