import React, { Component } from 'react';
import {Col, Form, Row, Table, Button} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import {FaSort, FaSortUp, FaSortDown} from 'react-icons/fa';
import {connect} from 'react-redux';

import 'bootstrap/dist/css/bootstrap.min.css';
import './style/ListPage.css'

class ListPage extends Component {
    constructor(props) {
        super(props);
        this.state={
            sort_state: 0,
        };
    }

    sort_asc() {
        let sorted = [...this.props.place_list]
        sorted.sort(function(a, b) {
            return a.rating - b.rating;
        });
        return sorted;
    }
    sort_desc() {
        let sorted = [...this.props.place_list]
        sorted.sort(function(a, b) {
            return b.rating - a.rating;
        });
        return sorted;
    }
    sort() {
        switch (this.state.sort_state) {
            case 0:
                return this.props.place_list;
            case 1:
                return this.sort_asc();
            case 2:
                return this.sort_desc();
            default:
                return this.props.place_list;
        }
    }
    sortAction() {

    }

    showListView(place_list) {
        let tbody = [];
        for (var i = 0; i < place_list.length; i++) {
            let row = <tr id={place_list[i].id} key={i} className='content'>
                        <td > {place_list[i].name} </td>
                        <td > {place_list[i].address} </td>
                        <td > {place_list[i].rating} </td>
                        <td className="picture"> <img src={place_list[i].picture} alt={place_list[i].name} /> </td>
                        <td >
                            <button className="custom-btn-update">Update</button>
                            <button className="custom-btn-delete" onClick={this.props.rowDeleteById}>Delete</button>
                        </td>
                    </tr>
    
            tbody.push(row)
        }
        return tbody;
    }
    
    render() {
        let sortIcon = <FaSort style={{marginLeft: "5px"}} />;
        if(this.state.sort_state === 1) {
            sortIcon = <FaSortUp style={{marginLeft: "5px"}} />
        } else if(this.state.sort_state === 2) {
            sortIcon = <FaSortDown style={{marginLeft: "5px"}} />
        }else {
            sortIcon = <FaSort style={{marginLeft: "5px"}} />
        }
        return (
            <>
                <Form.Group>
                        <Row className='test'>
                            <Col>
                                <Form.Control id="search-box" size="lg" type="text" placeholder="Enter Name to Search" bsPrefix="custom-input" required />
                            </Col>
                        </Row>
                </Form.Group>
                <br/>
                <div className="main">
                <Table bordered hover >
                    <thead className="header">
                        <tr style={{textAlign: "center"}}>
                            <th>NAME</th>
                            <th>ADDRESS</th>
                            <th onClick={this.sortAction}>
                                RATING
                                {sortIcon}
                            </th>
                            <th>PICTURE</th>
                            <th>ACTION</th>
                        </tr>
                    </thead>
                    <tbody >
                        {this.showListView(this.sort(this.props.place_list))}
                    </tbody>
                </Table>
                </div>
                <Link to="/create"><Button bsPrefix="custom-btn">Create New Tourist Place</Button></Link>
            </>
        );
    }
}

const mapStateToProps = state => {
    return {
        place_list: state.place_list
    }
}

export default connect(mapStateToProps, null)(ListPage);
