import React, { Component } from 'react';
import {Form,Row, Col} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

import './CreatePage.css';

class CreatePage extends Component {
    constructor(props) {
        super(props);

        this.state={
            name: null,
            address: null,
            rating: null,
            type: "beach",
            picture: null,
        }
    }

    render() {
        return (
            <div className="centered-wrapper">
                <Form.Group>
                    <Row>
                        <Form.Label column="lg" lg={3}>
                            Name:
                        </Form.Label>
                        <Col>
                            <Form.Control size="lg" type="text" bsPrefix="custom-input" required />
                        </Col>
                    </Row>
                    <br /> <br />
                    <Row>
                        <Form.Label column="lg" lg={3}>
                            Address:
                        </Form.Label>
                        <Col>
                            <Form.Control size="lg" type="text" bsPrefix="custom-input" required />
                        </Col>
                    </Row>
                    <br /> <br />
                    <Row>
                        <Form.Label column="lg" lg={3}>
                            Rating:
                        </Form.Label>
                        <Col>
                            <Form.Control size="lg" type="number" min="1" max="5" bsPrefix="custom-input" required />
                        </Col>
                    </Row>
                </Form.Group>
            </div>
        );
    }
}

export default CreatePage;
