import {FORM_VALUES_NAME} from './createPageTypes'
import {FORM_VALUES_ADDRESS} from './createPageTypes'
import {FORM_VALUES_RATING} from './createPageTypes'
import {FORM_VALUES_TYPE} from './createPageTypes'
import {FORM_VALUES_PICTURE} from './createPageTypes'

const initialState = {
    name: null,
    address: null,
    rating: null,
    type: "beach",
    picture: null,
}

export const createPageReducer = (state = initialState, action) => {
    switch(action.type) {
        case FORM_VALUES_NAME: return {
            ...state,
            name: action.payload
        }
        case FORM_VALUES_ADDRESS: return {
            ...state,
            address: action.payload
        }
        case FORM_VALUES_RATING: return {
            ...state,
            rating: action.payload
        }
        case FORM_VALUES_TYPE: return {
            ...state,
            type: action.payload
        }
        case FORM_VALUES_PICTURE: return {
            ...state,
            picture: action.payload
        }
        default: return {
            state
        }
    }
}
