import React, { Component } from 'react';
import CreatePage from './CreatePage2';
import ListPage from './ListPage';

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

class TouristPlace {
    constructor(id, name, address, rating, type) {
        this.id = id;
        this.name = name;
        this.address = address;
        this.rating = rating;
        this.type = type;
    }

    toString() {
        return this.name;
    }
}

class TouristApp extends Component {
    constructor(props) {
        super(props);

        this.state={
            inputs: {
                name: null,
                address: null,
                rating: null,
                type: "beach",
                picture: null,
            },
            typeList: ["Beach", "Hills", "Fountain", "Landmark"],
            touristPlace: []
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    handleReset(event) {
        [ "type", "name", "address", "rating"].map( id =>
            document.getElementById(id).value = ""   
        );

        this.setState({
            ...this.state,
            inputs: {
                ...this.state.inputs,
                name: "", address: "", rating: "", type: ""
            }
        });
    }

    handleSubmit(event) {
        const tourist_place = new TouristPlace(1, this.state.inputs.name, this.state.inputs.address, this.state.inputs.rating, this.state.inputs.type);
        var new_list = this.state.touristPlace.concat(tourist_place);
        this.setState({
            touristPlace: new_list
        })
        console.log(tourist_place);
        console.log(this.state);
    }

    handleInputChange(event) {
        let { id, value } = event.target;
        this.setState({
            ...this.state,
            inputs: {
                ...this.state.inputs,
                [id]: value
            }
        });
    }

    render() {
        return (
            <Router>
                <Switch>
                    <Route exact path='/'>
                        <CreatePage state={this.state} handleInputChange={this.handleInputChange} handleSubmit={this.handleSubmit} handleReset={this.handleReset} />
                    </Route>
                    <Route path='/create'>
                        <ListPage />
                    </Route>
                </Switch>
            </Router>
        );
    }
}

export default TouristApp;
