import React, { Component } from 'react';
import CreatePage from './CreatePage';
import ListPage from './ListPage';

import {
    BrowserRouter as Router,
    Switch,
    Route
  } from "react-router-dom";

class TouristPlace {
    constructor(id, name, address, rating, type, picture) {
        this.id = id;
        this.name = name;
        this.address = address;
        this.rating = rating;
        this.type = type;
        this.picture = picture;
    }

    toString() {
        return this.name;
    }
}

class TouristApp extends Component {
    constructor(props) {
        super(props);

        this.state={
            inputs: {
                name: null,
                address: null,
                rating: null,
                type: "beach",
                picture: null,
            },
            typeList: ["Beach", "Hills", "Fountain", "Landmark"],
            touristPlace: [],
            id: 1
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    handleReset(event) {
        [ "type", "name", "address", "rating"].map( id =>
            document.getElementById(id).value = null  
        );
        document.getElementById('picture').files = null;

        this.setState({
            ...this.state,
            inputs: {
                ...this.state.inputs,
                name: null, address: null, rating: null, type: null, picture: null
            }
        });
    }

    toBase64 = file => new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });

    async handleSubmit(event) {
        const base_string = await this.toBase64(this.state.inputs.picture);

        const tourist_place = new TouristPlace(this.state.id, this.state.inputs.name,
                                                this.state.inputs.address, this.state.inputs.rating,
                                                this.state.inputs.type, base_string);

        var new_list = this.state.touristPlace.concat(tourist_place);
        this.setState({
            ...this.state,
            touristPlace: new_list,
            id: this.state.id + 1
        })

        this.handleReset(this);
    }

    handleInputChange(event) {
        let {id, value} = event.target;
        if(id === 'picture') {
            this.setState({
                ...this.state,
                inputs: {
                    ...this.state.inputs,
                    picture: event.target.files[0]
                }
            });
            return;
        }

        this.setState({
            ...this.state,
            inputs: {
                ...this.state.inputs,
                [id]: value
            }
        });
    }

    render() {
        return (
            <Router>
                <Switch>
                    <Route exact path='/'>
                        <ListPage state={this.state} />
                    </Route>
                    <Route exact path='/create'>
                        <CreatePage state={this.state} handleInputChange={this.handleInputChange} handleSubmit={this.handleSubmit} handleReset={this.handleReset} />
                    </Route>
                </Switch>
            </Router>
        );
    }
}

export default TouristApp;
