import React, { Component } from 'react';
import CreatePage from './CreatePage2';

class TouristPlace {
    constructor(id, name, address, rating, type) {
        this.id = id;
        this.name = name;
        this.address = address;
        this.rating = rating;
        this.type = type;
    }

    toString() {
        return this.name;
    }
}

class TouristApp extends Component {
    constructor(props) {
        super(props);

        this.state={
            inputs: {
                name: null,
                address: null,
                rating: null,
                type: "beach",
                picture: null,
            },
            typeList: ["Beach", "Hills", "Fountain", "Landmark"],
            touristPlace: []
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    handleReset(event) {
        document.getElementById('test-form').reset();
    }

    handleSubmit(event) {
        const tourist_place = new TouristPlace(1, this.state.name, this.state.address, this.state.rating, this.state.type);
        var new_list = this.state.touristPlace.concat(tourist_place);
        this.setState({
            touristPlace: new_list
        })
        console.log(tourist_place);
        console.log(this.state);
    }

    handleInputChange(event) {
        let { id, value } = event.target;
        this.setState({
            ...this.state,
            inputs: {
                ...this.state.inputs,
                [id]: value
            }
        });
    }

    handleInputChange2(event) {
        const target = event.target;

        switch(target.id) {
            case "name":
                this.setState({
                    name: target.value
                })
                break
            case "address":
                this.setState({
                    address: target.value
                })
                break
            case "rating":
                this.setState({
                    rating: target.value
                })
                break
            case "type":
                this.setState({
                    type: target.value
                })
                break
            default:
        }
        //console.log("test")
    }

    render() {
        return (
            <CreatePage state={this.state} handleInputChange={this.handleInputChange} handleSubmit={this.handleSubmit} handleReset={this.handleReset} />
        );
    }
}

export default TouristApp;
